import React, { Fragment, useState } from 'react'
import { Link } from 'react-router-dom'
import axios from 'axios'
import Swal from 'sweetalert2'

export default function AgregarLugar() {

    const [grupo, setGrupo] = useState('')
    const [nombre, setNombre] = useState('')
    const [latitud, setLatitud] = useState(0)
    const [longitud, setLongitud] = useState(0)


    const agregarLugar = e => {

        e.preventDefault()
        console.log(grupo)
        console.log(nombre)
        console.log(latitud)
        axios({
            "method": "POST",
            "url": "http://localhost:5000//agregarLugarGrupoInteres",
            "params": {
                "grupo": grupo,
                "latitud": latitud,
                "longitud": longitud,
                "nombre": nombre,
            }

        }).then((response) => {
            console.log(response);
            Swal.fire({
                title: 'Lugar agregado!',
                text: 'El lugar se agrego correctamente',

            }).then((result) => {
                if (result.value) {
                    let link = '/'
                    window.location.href = link
                }
            })
        })
            .catch((error) => {
                console.log(error);
                Swal.fire({
                    title: "Ha ocurrido un error!",
                    text: "Verifique que los datos ingresados sean correctos",
                    icon: "error",
                });
            });
    }




    return (
        <Fragment>


            <div align="center">

                            <form className="col-sm-4" onSubmit={agregarLugar}>
                    <div class="form-group">
                        <br />
                        <label for="exampleFormControlSelect1">Seleccionar un grupo</label>
                        <select class="form-control" id="exampleFormControlSelect1" onChange={e => setGrupo(e.target.value)} >
                            <option value="CentrosEmergencias">Centros de Urgencia</option>
                            <option value="Farmacias">Farmacias</option>
                            <option value="facultades">Facultades</option>
                            <option value="Supermercados">Supermercados</option>
                            <option value="Cervecerias">Cervecerias</option> {/*selected*/}
                        </select>
                    </div>
                    <div class="form-group">
                        <br />

                        <input type="text" class="form-control" required placeholder="Ingrese el nombre del lugar" // id="exampleInput" //aria-describedby="emailHelp"
                            onChange={e => setNombre(e.target.value)} />

                    </div>

                    <div class="form-group">
                    <br />

                        <input class="form-control" required placeholder="Ingrese la latitud ej: -32.484402"// id="exampleInput" //aria-describedby="emailHelp"
                            onChange={e => setLatitud(e.target.value)} />

                    </div>


                    <div class="form-group">
                    <br />

                        <input class="form-control"  required  placeholder="Ingrese la longitud ej: -58.232802"//id="exampleInputPassword1"
                            onChange={e => setLongitud(e.target.value)} />
                    </div>

                    <button type="submit" class="btn btn-success">Agregar Lugar</button>
                </form>
            </div>
        </Fragment>
    )



}
