import React, {useState, useEffect, Fragment} from 'react'
import axios from 'axios'
import L from 'leaflet'
import {Link} from 'react-router-dom'
import {Map, TileLayer, Marker, Popup} from 'react-leaflet';
import uuid from 'react-uuid'

export default function Facultades(){
    // var listado = []
    // var posicion =[]
    const [ubicaciones, setUbicaciones] = useState ([])
    const [radio,setRadio] = useState([])

    /*var browserLat;
    var browserLong;  

    navigator.geolocation.getCurrentPosition(function(position) {
    browserLat =  position.coords.latitude;
    browserLong = position.coords.longitude;

    marker_actual = L.marker([browserLat,browserLong]).addTo(map);
    marker_actual.bindPopup('<b>Hola </b><br>Tu estas aqui').openPopup();
    map.setView([browserLat,browserLong], 18);  

    console.log(browserLat);
    console.log(browserLong);
    }, function(err) {
    console.error(err);
    });

*/
   /*
    map.locate({setView: true, maxZoom: 16});//mostrar iconito

    function onLocationFound(e) {
        var radius = e.accuracy / 2;

       L.marker(e.latlng).addTo(map)
        .bindPopup("Tu estas aqui, con " + radius + " metros de aproximacion").openPopup();

       L.circle(e.latlng, radius).addTo(map);
        }
        function onLocationError(e) {
        alert(e.message);
       }

       */





    const actualizarListado = () => {
        axios.get('http://localhost:5000/listaGrupo?grupo=facultades').then((res) => {
        console.log(res.data)  
        setUbicaciones(res.data)
      })
      .catch((error) =>{
          console.log(error);
      })  
  }

  const radioActual = () => {
    navigator.geolocation.getCurrentPosition(function(position) {
    console.log(position.coords.latitude, position.coords.longitude);

    axios.get(`http://localhost:5000/lugaresRadio?grupo=facultades&longitud=${position.coords.longitude}&latitud=${position.coords.latitude}`).then((res) => {
    // console.log(res.data)         
    setRadio(res.data)
    console.log(radio)

      })
      .catch((error) =>{
          console.log(error);
      })        
    });    

}


    const myIcon= L.icon({
        iconUrl:'https://cdn.icon-icons.com/icons2/1179/PNG/128/1490127538-school-object-study-student12_82093.png',
        iconSize:[25,41],
        iconAnchor:[12.5, 41],
        popupAnchor:[0,-41]

    })

    const iconoPosActual = {
        iconUrl : 'my-icon.png',
        iconSize: [38, 95],
        iconAnchor: [22, 94],
        popupAnchor: [-3, -76],
        shadowUrl: 'my-icon-shadow.png',
        shadowSize: [68, 95],
        shadowAnchor: [22, 94]
    }


    const style ={
        height: '650px',
        width: '650px',


    }

    const listadito={
        display :'flex',
        justifyContent: 'space-around'
    }



    useEffect(() => {

        actualizarListado();
        radioActual();
        //navigator.geolocation.getCurrentPosition

        //map.on('locationfound', onLocationFound);
        //map.on('locationerror', onLocationError);

        ;


    }, [])


    return(
    <Fragment>

    <div style={listadito}> 

       <Map center={[-32.484402, -58.232802]} zoom={14} style={style} className="mt-4" >
           <TileLayer
            attribution='&amp;copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
           />

            {ubicaciones.map(ubi => (

                <Marker 
                key = {uuid()}
                position = {[ubi.latitud, ubi.longitud]} icon ={myIcon}> 
                <Popup>
                    {ubi.nombre}
                </Popup>

                </Marker>
            ) )}          

       </Map>


       <div class="mt-5" align="center">


                    <div >
                    <h2>Facultades</h2>
                    <p>Que se encuentran en un radio de 5 Km. de ud.</p>

                            <table class="table col-xl-6" >
                                <thead>
                                <tr>
                                    <th>Nombre</th>
                                    <th>Distancia</th>
                                </tr>
                                </thead>
                                <tbody>
                                    {radio.map(ra =>(
                                    <tr>
                                    <td>{ra[0]}</td>  
                                    <td align="center">{ra[1]} kms</td>                                 
                                    </tr>   
                                    ))}
                                </tbody>
                            </table>

                            </div>
                </div>
        </div>  

       </Fragment>
        )   
}
